mapper.delete:
delete from anode_tbl
where id in

  #{ids}

mapper.select:
select

from anode_tbl


    and id = #{id}


    and province = #{province}


    and company = #{company}


    and s_company = #{s_company}


    and e_density = #{e_density}


    and true_density = #{true_density}


    and compressive = #{compressive}


    and carbon = #{carbon}


    and flexural = #{flexural}


    and room = #{room}


    and thermal = #{thermal}


    and ash = #{ash}


    and size = #{size}


    and bowl_sum = #{bowl_sum}


    and bowl_distance = #{bowl_distance}


    and in_diameter = #{in_diameter}


    and out_diameter = #{out_diameter}


    and slot = #{slot}


    and today_price = #{today_price}


    and remarks = #{remarks}


    and reporter = #{reporter}


    and create_time = #{create_time}


    and update_time = #{update_time}


    and create_time >= #{b_time}


    and create_time <= #{e_time}



  order by create_time DESC
